import{AddTodo} from "./MyComponents/AddTodo";
import React, { useState } from 'react';


function App() {

   const onDelete=(todo)=>{
    console.log("I am onDelete of todo ",todo);
   // let index = todos.indexOf(todo);
   // todos.splice(index, 1);
    setTodos(todos.filter((e)=>{
      return e!==todo;
    }))

   }
   const addTodo =(title,desc)=>{
    console.log("I am adding this todo ",title,desc)
     let slno = todos[todos.length-1].slno+1;
    const myTodo ={
      slno:slno,
      title:title,
      desc:desc,
    }
    setTodos([...todos, myTodo]);
    console.log(myTodo)
   }

   const [todos, setTodos] = useState([
    {
      slno: 1,
      title: "Start making !X!",
      desc: "you need to learn the react and use components to make the twitter "  
   },
   {
    slno: 2,
    title: " frame work and data !X!",
    desc: "you need to learn the Go and use framework to make the twitter "  
 },
 {
  slno: 3,
  title: "Depoly !X!",
  desc: "you need to learn the AWS and Depoly the X "  
},
  ]);

return (

    <>

    <Header title="My Todo List" searchBar={true} />
    <AddTodo addTodo={addTodo}/>
    <Todos todos={todos} onDelete={onDelete} />
    <Footer/>

    </>

  );
}

export default App;
